// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;
pragma experimental ABIEncoderV2;

contract ChapterHistory {
    mapping(address => string[]) public chapterHistories;
    constructor() public {
    }
    // Event to emit when chapter history is added
    event ChapterHistoryAdded(address user, string chapter);

    // Function to add a chapter history for a user
    function addChapterHistory(string memory chapter) public {
        chapterHistories[msg.sender].push(chapter);
        emit ChapterHistoryAdded(msg.sender, chapter);
    }

    // Function to get all chapter histories for a user
    function getChapterHistories() public view returns (string[] memory) {
        return chapterHistories[msg.sender];
    }
}
