// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;
pragma experimental ABIEncoderV2;

contract ChatHistory {
    address public admin;  // Admin address that can add chat history
    mapping(bytes32 => ChatBlock[]) public userChatHistory;  // Mapping from hashed UID to an array of ChatBlocks

    struct ChatBlock {
        uint timestamp;           // Timestamp of when the block was created
        string[] messages;        // Array of messages in the block
    }

    // Event to emit when a chat block is added
    event ChatBlockAdded(bytes32 indexed uidHash, uint timestamp, string[] messages);

    // Modifier to restrict functions to only the admin
    modifier onlyAdmin() {
        require(msg.sender == admin, "Only admin can perform this action");
        _;
    }

    // Constructor to set the admin
    constructor() public {
        admin = 0xCE486c2a45b2Cd92f9381100bb7a288618074286;
    }

    // Function to add a chat block (multiple messages) for a user based on their UID
    function addChatBlock(string memory uid, string[] memory messages) public{
        bytes32 uidHash = keccak256(abi.encodePacked(uid));  // Hash the UID into a bytes32 key
        uint timestamp = block.timestamp;  // Current timestamp

        // Create a new chat block and add messages to it
        ChatBlock memory newBlock = ChatBlock({
            timestamp: timestamp,
            messages: messages
        });

        userChatHistory[uidHash].push(newBlock);  // Store the chat block in the user's history

        // Emit an event for the new chat block
        emit ChatBlockAdded(uidHash, timestamp, messages);
    }

    // Function to get all chat blocks for a user based on their UID
    function getChatHistory(string memory uid) public view returns (ChatBlock[] memory) {
        bytes32 uidHash = keccak256(abi.encodePacked(uid));  // Hash the UID into a bytes32 key
        return userChatHistory[uidHash];
    }

    // Function to get a specific chat block (by index) for a user based on their UID
    function getChatBlock(string memory uid, uint index) public view returns (ChatBlock memory) {
        bytes32 uidHash = keccak256(abi.encodePacked(uid));  // Hash the UID into a bytes32 key
        require(index < userChatHistory[uidHash].length, "Chat block index out of range");
        return userChatHistory[uidHash][index];
    }

}
